import React from 'react';
import styled from 'styled-components';
// import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	// const TOTAL_SLIDES = 1;
	// const [currentSlide, setCurrentSlide] = useState(0);
	// const slideRef = useRef(null);

	// // Next 버튼 클릭 시
	// const NextSlide = () => {
	// 	if (currentSlide >= TOTAL_SLIDES) {
	// 		// 더 이상 넘어갈 슬라이드가 없으면
	// 		setCurrentSlide(0); // 1번째 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide + 1);
	// 	}
	// };
	// // Prev 버튼 클릭 시
	// const PrevSlide = () => {
	// 	if (currentSlide === 0) {
	// 		setCurrentSlide(TOTAL_SLIDES); // 마지막 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide - 1);
	// 	}
	// };

	// useEffect(() => {
	// 	slideRef.current.style.transition = 'all 0.5s ease-in-out';
	// 	slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 에니메이션을 만듭니다.
	// }, [currentSlide]);

	return (
		<Container>
			<Wrap>
				<Text>
					<h1>LEARN REACT PROJECT</h1>
				</Text>
				<SliderContainer>
					<img src={process.env.PUBLIC_URL + '/img/main_bg01.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg02.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg03.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg04.jpg'} />
				</SliderContainer>
				{/* <Center>
					<Button onClick={PrevSlide}>Prev</Button>
					<Button onClick={NextSlide}>Next</Button>
				</Center> */}
			</Wrap>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 1200px;
	margin-top: 200px;
`;

const Wrap = styled.div`
	width: 100%;
	max-width: 75%;
	height: 100%;
`;
const Button = styled.div`
	all: unset;
	padding: 1em 2em;
	margin: 2em 2em;
	color: burlywood;
	border-radius: 10px;
	border: 1px solid burlywood;
	cursor: pointer;
	&:hover {
		background-color: burlywood;
		color: #fff;
	}
`;
const SliderContainer = styled.div`
	width: 100%;

	/* margin: 0 auto; */
	height: 700px;
	margin-bottom: 2em;
	display: flex; // 이미지들을 가로로 나열합니다.
	justify-content: space-between;

	img {
		width: 30%;
		height: 100%;

		object-fit: cover;
		&:hover {
			transform: scaleX(1.5);
			transition: all 0.3s;
		}
	}
`;

const Text = styled.div`
	text-align: center;
	color: burlywood;
	p {
		color: #fff;
		font-size: 20px;
		background-color: burlywood;
		display: inline-block;

		border-radius: 50px;
		padding: 0.5em 1em;
	}
`;
const Center = styled.div`
	text-align: center;
`;
