import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	const slideRef = useRef(null);
	const [currentImgOrder, setcCurrentImgOrder] = useState(0);
	const IMG_WIDTH = 500;
	const slideRange = currentImgOrder * IMG_WIDTH;

	useEffect(() => {
		slideRef.current.style.transition = 'all 0.5s ease-in-out';
		slideRef.current.style.transform = `translateX(-${slideRange}px)`;
	}, [currentImgOrder]);

	const moveToNextSlide = () => {
		if (currentImgOrder === 2) return;
		setcCurrentImgOrder(currentImgOrder + 1);
	};

	const moveToPrevSlide = () => {
		if (currentImgOrder === 0) return;
		setcCurrentImgOrder(currentImgOrder - 1);
	};

	return (
		<Container>
			<SlideWrapper ref={slideRef}>
				<Content></Content>
				<Content></Content>
				<Content></Content>
				<Content></Content>
			</SlideWrapper>
			<button onClick={moveToPrevSlide}>prev</button>
			<button onClick={moveToNextSlide}>next</button>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 800px;
`;

const SlideWrapper = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
	height: 100%;
`;

const Content = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	width: 90%;
	height: 500px;
	background-color: red;
	margin-right: 10px;
`;
