import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	const [index, setIndex] = useState(0);

	return (
		<Container>
			<Wrap>
				<SlideWrap>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg01.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg02.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg03.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg04.jpg'} alt="movielist" />
						</a>
					</Content>
				</SlideWrap>
			</Wrap>
			<button onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)`, transition: 'all 3s' }}>
				prev
			</button>
			<button onClick={moveToNextSlide}>next</button>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 800px;
`;

const Wrap = styled.div`
	width: 100%;
`;
const SlideWrap = styled.div`
	width: 100%;
	height: 100%;
`;

const Content = styled.div`
	margin: 0 auto;

	width: 80%;
	height: 500px;

	a {
		width: 100%;
		height: 100%;

		&:hover {
			/* transform: scale(1.5);
			transition: transform 1s; */
			filter: brightness(70%);
		}

		img {
			width: 100%;
			height: 100%;
			object-fit: cover;
		}
	}
`;
