import React from 'react';
import styled from 'styled-components';
// import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	// const TOTAL_SLIDES = 1;
	// const [currentSlide, setCurrentSlide] = useState(0);
	// const slideRef = useRef(null);

	// // Next 버튼 클릭 시
	// const NextSlide = () => {
	// 	if (currentSlide >= TOTAL_SLIDES) {
	// 		// 더 이상 넘어갈 슬라이드가 없으면
	// 		setCurrentSlide(0); // 1번째 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide + 1);
	// 	}
	// };
	// // Prev 버튼 클릭 시
	// const PrevSlide = () => {
	// 	if (currentSlide === 0) {
	// 		setCurrentSlide(TOTAL_SLIDES); // 마지막 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide - 1);
	// 	}
	// };

	// useEffect(() => {
	// 	slideRef.current.style.transition = 'all 0.5s ease-in-out';
	// 	slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 에니메이션을 만듭니다.
	// }, [currentSlide]);

	return (
		<Container>
			<Wrap>
				<Title>
					<h4>02</h4>
					<h2>Front-End Web Develop</h2>
					<h3>Endeavors for more reasonable, logical design. I have learned to create clearer, more logical and reasonable design and to increase efficiency of collaboration.</h3>
					<View>
						<a href="main">VIEW</a>
					</View>
				</Title>
				<SliderContainer>
					<img src={process.env.PUBLIC_URL + '/img/main_bg01.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg02.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg03.jpg'} />
					<img src={process.env.PUBLIC_URL + '/img/main_bg04.jpg'} />
				</SliderContainer>
				{/* <Center>
					<Button onClick={PrevSlide}>Prev</Button>
					<Button onClick={NextSlide}>Next</Button>
				</Center> */}
			</Wrap>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 1200px;
	margin-top: 200px;
`;

const Wrap = styled.div`
	width: 100%;
	max-width: 75%;
	height: 100%;
	margin: 0 auto;
`;
const Title = styled.div`
	z-index: 2;
	position: absolute;
	top: 0;
	right: 0;
	margin-left: 50px;
	width: 43%;
	padding-top: 100px;
	@media ${({ theme }) => theme.device.laptop} {
		z-index: 2;
		position: absolute;
		top: 0;
		right: 0;
		margin-left: 50px;
		width: 43%;
		padding-top: 200px;
	}
	@media ${({ theme }) => theme.device.tablet} {
		z-index: 2;
		position: absolute;
		top: 0;
		right: 0;
		margin-left: 50px;
		width: 43%;
		padding-top: 100px;
	}
	@media ${({ theme }) => theme.device.mobile} {
		z-index: 2;
		position: absolute;
		top: -450px;
		right: 10%;
		width: 100%;
		max-width: 80%;
		padding-top: 100px;
		text-align: center;
	}
	@media only screen and (max-width: 600px) {
	}

	h4 {
		font-weight: 400;
		color: #fff;
		font-size: 20px;
		@media ${({ theme }) => theme.device.laptop} {
			font-family: 'Montserrat';
			font-weight: 500;
			color: #fff;
			font-size: 18px;
		}
		@media ${({ theme }) => theme.device.tablet} {
			font-family: 'Montserrat';
			font-weight: 500;
			color: #fff;
			font-size: 18px;
		}
		@media ${({ theme }) => theme.device.mobile} {
			font-family: 'Montserrat';
			font-weight: 500;
			color: ${({ theme }) => theme.colors.black_color};
			font-size: 18px;
		}
	}

	h2 {
		width: 60%;
		font-weight: ${({ theme }) => theme.fonts.weightRegular};
		color: ${({ theme }) => theme.colors.white_color};
		font-size: 60px;
		margin-top: 20px;
		line-height: 120%;
		@media ${({ theme }) => theme.device.laptop} {
			width: 70%;
			font-family: 'Montserrat';
			font-weight: 500;
			color: #fff;
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
		@media ${({ theme }) => theme.device.tablet} {
			width: 70%;
			font-family: 'Montserrat';
			font-weight: 500;
			color: #fff;
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
		@media ${({ theme }) => theme.device.mobile} {
			display: inline-block;
			width: 100%;
			font-family: 'Montserrat';
			font-weight: 700;
			color: ${({ theme }) => theme.colors.black_color};
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
		@media only screen and (max-width: 600px) {
		}
	}
	h3 {
		width: 50%;
		font-weight: ${({ theme }) => theme.fonts.weightRegular};
		color: ${({ theme }) => theme.colors.white_color};
		font-size: ${({ theme }) => theme.fonts.fontSemiMedium};
		line-height: 160%;
		margin-top: 40px;
		@media ${({ theme }) => theme.device.laptop} {
			width: 70%;

			font-weight: 500;
			color: #fff;
			font-size: 18px;
			line-height: 160%;
			margin-top: 40px;
		}
		@media ${({ theme }) => theme.device.tablet} {
			width: 70%;
			font-weight: 500;
			color: #fff;
			font-size: 18px;
			line-height: 160%;
			margin-top: 40px;
		}
		@media ${({ theme }) => theme.device.mobile} {
			display: inline-block;
			width: 100%;
			font-weight: 500;
			color: ${({ theme }) => theme.colors.black_color};
			font-size: 18px;
			line-height: 160%;
			margin-top: 40px;
		}
		@media only screen and (max-width: 600px) {
		}
	}
`;
const Button = styled.div`
	all: unset;
	padding: 1em 2em;
	margin: 2em 2em;
	color: burlywood;
	border-radius: 10px;
	border: 1px solid burlywood;
	cursor: pointer;
	&:hover {
		background-color: burlywood;
		color: #fff;
	}
`;
const SliderContainer = styled.div`
	width: 100%;

	/* margin: 0 auto; */
	height: 700px;
	margin-bottom: 2em;
	display: flex; // 이미지들을 가로로 나열합니다.
	justify-content: space-between;

	img {
		width: 25%;
		height: 100%;

		object-fit: cover;
		&:hover {
			transform: scaleX(1.5);
			transition: all 0.3s;
		}
	}
`;

const Text = styled.div`
	text-align: center;
	color: burlywood;
	p {
		color: #fff;
		font-size: 20px;
		background-color: burlywood;
		display: inline-block;

		border-radius: 50px;
		padding: 0.5em 1em;
	}
`;
const Center = styled.div`
	text-align: center;
`;
