import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	// const slideRef = useRef(null);
	// const [currentImgOrder, setcCurrentImgOrder] = useState(0);
	// const IMG_WIDTH = 300;
	// const slideRange = currentImgOrder * IMG_WIDTH;

	// useEffect(() => {
	// 	slideRef.current.style.transition = 'all 0.5s ease-in-out';
	// 	slideRef.current.style.transform = `translate3d(${slideRange}%)`;
	// }, [currentImgOrder]);

	// const moveToNextSlide = () => {
	// 	if (currentImgOrder === 2) return;
	// 	setcCurrentImgOrder(currentImgOrder + 1);
	// };

	// const moveToPrevSlide = () => {
	// 	if (currentImgOrder === 0) return;
	// 	setcCurrentImgOrder(currentImgOrder - 1);
	// };

	return (
		<Container>
			<Wrap>
				<SlideWrap>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg01.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg02.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg03.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg04.jpg'} alt="movielist" />
						</a>
					</Content>
				</SlideWrap>
			</Wrap>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 800px;
`;

const Wrap = styled.div`
	width: 100%;
`;
const SlideWrap = styled.div`
	display: flex;
	justify-content: space-between;
	max-width: 75%;
	margin: 0 auto;
	width: 100%;
	height: 100%;
`;

const Content = styled.div`
	display: flex;

	width: 80%;
	height: 500px;

	a {
		width: 100%;
		height: 100%;

		&:hover {
			/* transform: scale(1.5);
			transition: transform 1s; */
			filter: brightness(70%);
		}

		img {
			width: 100%;
			height: 100%;
			object-fit: cover;
		}
	}
`;
