import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	const data = [
		{ id: 0, name: 'MOVIELIST', src: '/img/todolist.png', url: 'https://kimahr.github.io/movie-list/', material: 'Recycled' },
		{ id: 1, name: 'TODOLIST', src: '/img/todolist.png', url: 'https://kimahr.github.io/todolist/', material: 'Recycled' },
		{ id: 2, name: 'PORTFOLIO', src: '/img/portfolio.jpg', url: 'https://github.com/KimAhR/aarreumkim/', material: 'Recycled' },
		{ id: 3, name: 'DIARY', src: '/img/macbook.png', url: 'https://kimahr.github.io/movielist/', material: 'Leather' },
	];
	const [index, setIndex] = useState(0);

	const slideRef = useRef(null);

	useEffect(() => {
		slideRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)));
	});

	return (
		<Container>
			<Wrap>
				<SlideWrap>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg01.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg02.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg03.jpg'} alt="movielist" />
						</a>
					</Content>
					<Content>
						<a href="https://kimahr.github.io/movie-list/">
							<img src={process.env.PUBLIC_URL + '/img/main_bg04.jpg'} alt="movielist" />
						</a>
					</Content>
				</SlideWrap>
			</Wrap>
			{data.map((item) => (
				<button key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)`, transition: 'all 3s' }}>
					prev
				</button>
			))}
			{data.map((item) => (
				<button key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(-${index}%)`, transition: 'all 3s' }}>
					next
				</button>
			))}
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 800px;
`;

const Wrap = styled.div`
	width: 100%;
`;
const SlideWrap = styled.div`
	width: 100%;
	height: 100%;
`;

const Content = styled.div`
	margin: 0 auto;

	width: 80%;
	height: 500px;

	a {
		width: 100%;
		height: 100%;

		&:hover {
			/* transform: scale(1.5);
			transition: transform 1s; */
			filter: brightness(70%);
		}

		img {
			width: 100%;
			height: 100%;
			object-fit: cover;
		}
	}
`;
