import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	// const TOTAL_SLIDES = 3;
	// const [currentSlide, setCurrentSlide] = useState(0);
	// const slideRef = useRef(null);

	// // Next 버튼 클릭 시
	// const NextSlide = () => {
	// 	if (currentSlide >= TOTAL_SLIDES) {
	// 		// 더 이상 넘어갈 슬라이드가 없으면
	// 		setCurrentSlide(0); // 1번째 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide + 1);
	// 	}
	// };
	// // Prev 버튼 클릭 시
	// const PrevSlide = () => {
	// 	if (currentSlide === 0) {
	// 		setCurrentSlide(TOTAL_SLIDES); // 마지막 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide - 1);
	// 	}
	// };

	// useEffect(() => {
	// 	slideRef.current.style.transition = 'all 0.5s ease-in-out';
	// 	slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 에니메이션을 만듭니다.
	// }, [currentSlide]);

	return (
		<Container id="project">
			<Wrap>
				<TitleWrap>
					<Title>
						<h1>
							LEARN
							<br />
							REACT
							<br /> PROJECT
						</h1>
						<h3>
							React 원리 이해 · React의 기본 구조 및 실행 방법 이해 · 컴포넌트 생성 및 라이프 사이클 · SPA(Single Page Application)의 이해 및 구현, 웹 페이지 개선 · 클래스 컴포넌트에서 함수형
							컴포넌트의 비교 및 구현 (React Hooks), 웹 페이지 배포 · Github을 활용한 프로젝트 배포 등을 배웠습니다.
						</h3>
					</Title>
				</TitleWrap>
				<ContentWrap>
					<Content01>
						<a href="https://kimahr.github.io/movie-list/">
							<ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.jpg'} />
							</ImgArea>
							{/* <TextWrap>
								<p>MOVIELIST</p>
							</TextWrap> */}
							<HoverWrap>
								<span>MOVIELIST</span>
							</HoverWrap>
						</a>
					</Content01>
					<Content02>
						<a href="https://kimahr.github.io/todolist/">
							<ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/todolist.jpg'} />
							</ImgArea>
							{/* <TextWrap>
								<p>TODOLIST</p>
							</TextWrap> */}
							<HoverWrap>
								<span>TODOLIST</span>
							</HoverWrap>
						</a>
					</Content02>
					<Content03>
						<a href="https://kimahr.github.io/todolist/">
							<ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/diary.png'} />
							</ImgArea>
							{/* <TextWrap>
								<p>감정일기</p>
							</TextWrap> */}
							<HoverWrap>
								<span>감정일기</span>
							</HoverWrap>
						</a>
					</Content03>
					<Content04>
						<a href="https://kimahr.github.io/todolist/">
							{/* <ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.png'} />
							</ImgArea> */}
							{/* <TextWrap>
								<p>PORTFOLIO</p>
							</TextWrap> */}
							<HoverWrap>
								<span>PORTFOLIO</span>
							</HoverWrap>
						</a>
					</Content04>
				</ContentWrap>
				{/* <Center>
					<Button onClick={PrevSlide}>Prev</Button>
					<Button onClick={NextSlide}>Next</Button>
				</Center> */}
			</Wrap>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 1200px;
`;

const Wrap = styled.div`
	width: 100%;
	max-width: 75%;
	height: 100%;
	margin: 0 auto;
	display: flex;
	justify-content: space-around;
	&::before {
		display: block;
		content: '';
		width: 1px;
		height: 84%;
		background-color: ${({ theme }) => theme.colors.black_color};
	}
`;
const TitleWrap = styled.div`
	z-index: 2;

	width: 100%;
	height: 100%;
	max-width: 30%;
	margin: 0 auto;

	/* background-color: #47a992; */
	@media ${({ theme }) => theme.device.laptop} {
		z-index: 2;
		position: relative;

		width: 100%;
		max-width: 80%;
		margin: 0 auto;

		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.tablet} {
		width: 100%;
		max-width: 80%;
		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.mobile} {
		display: flex;
		flex-direction: column;
	}
`;

const Title = styled.div`
	width: 80%;

	margin: 0 auto;

	@media ${({ theme }) => theme.device.laptop} {
	}

	h1 {
		width: 100%;
		/* writing-mode: vertical-lr;
		text-orientation: upright; */
		font-weight: 600;
		color: ${({ theme }) => theme.colors.black_color};
		font-size: 3.5rem;
		text-align: left;
		line-height: 200%;
		@media ${({ theme }) => theme.device.tablet} {
			width: 100%;

			font-weight: 600;
			color: ${({ theme }) => theme.colors.black_color};
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
		@media ${({ theme }) => theme.device.mobile} {
			text-align: center;
			width: 100%;

			font-weight: 700;
