import React from 'react';
import styled from 'styled-components';
import { useState, useRef, useEffect } from 'react';

const SlideComponent = () => {
	// const TOTAL_SLIDES = 3;
	// const [currentSlide, setCurrentSlide] = useState(0);
	// const slideRef = useRef(null);

	// // Next 버튼 클릭 시
	// const NextSlide = () => {
	// 	if (currentSlide >= TOTAL_SLIDES) {
	// 		// 더 이상 넘어갈 슬라이드가 없으면
	// 		setCurrentSlide(0); // 1번째 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide + 1);
	// 	}
	// };
	// // Prev 버튼 클릭 시
	// const PrevSlide = () => {
	// 	if (currentSlide === 0) {
	// 		setCurrentSlide(TOTAL_SLIDES); // 마지막 사진으로 넘어갑니다.
	// 		// return;  // 클릭이 작동하지 않습니다.
	// 	} else {
	// 		setCurrentSlide(currentSlide - 1);
	// 	}
	// };

	// useEffect(() => {
	// 	slideRef.current.style.transition = 'all 0.5s ease-in-out';
	// 	slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 에니메이션을 만듭니다.
	// }, [currentSlide]);

	return (
		<Container>
			<Wrap>
				<TitleWrap>
					<Title>
						<h1>
							LEARN
							<br /> REACT PROJECT
						</h1>
					</Title>
				</TitleWrap>

				<ContentWrap>
					<Content01>
						<HoverWrap>
							<h3>movielist</h3>
							<span>view</span>
						</HoverWrap>

						<a href="https://kimahr.github.io/todolist/">
							<ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.jpg'} />
							</ImgArea>
							<TextWrap>
								<p>#REACT</p>
							</TextWrap>
						</a>
					</Content01>
					<Content02>
						<HoverWrap>
							<h3>movielist</h3>
							<span>view</span>
						</HoverWrap>
						<a href="https://kimahr.github.io/todolist/">
							{/* <ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.png'} />
							</ImgArea> */}
							<TextWrap>
								<p>#REACT</p>
							</TextWrap>
						</a>
					</Content02>
					<Content03>
						<HoverWrap>
							<h3>movielist</h3>
							<span>view</span>
						</HoverWrap>
						<a href="https://kimahr.github.io/todolist/">
							{/* <ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.png'} />
							</ImgArea> */}
							<TextWrap>
								<p>#REACT</p>
							</TextWrap>
						</a>
					</Content03>
					<Content04>
						<HoverWrap>
							<h3>movielist</h3>
							<span>view</span>
						</HoverWrap>
						<a href="https://kimahr.github.io/todolist/">
							{/* <ImgArea>
								<img src={process.env.PUBLIC_URL + '/img/movielist.png'} />
							</ImgArea> */}
							<TextWrap>
								<p>#REACT</p>
							</TextWrap>
						</a>
					</Content04>
				</ContentWrap>
				{/* <Center>
					<Button onClick={PrevSlide}>Prev</Button>
					<Button onClick={NextSlide}>Next</Button>
				</Center> */}
			</Wrap>
		</Container>
	);
};

export default SlideComponent;

const Container = styled.div`
	width: 100%;
	height: 1200px;
`;

const Wrap = styled.div`
	width: 100%;
	max-width: 75%;
	height: 100%;
	margin: 0 auto;
	display: flex;
	justify-content: space-between;
`;
const TitleWrap = styled.div`
	z-index: 2;

	width: 100%;
	height: 100%;
	max-width: 30%;
	margin: 0 auto;

	background: ${({ theme }) => theme.colors.black_color};
	@media ${({ theme }) => theme.device.laptop} {
		z-index: 2;
		position: relative;

		width: 100%;
		max-width: 80%;
		margin: 0 auto;

		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.tablet} {
		width: 100%;
		max-width: 80%;
		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.mobile} {
		display: flex;
		flex-direction: column;
	}
`;

const Title = styled.div`
	width: 80%;

	margin: 50px auto;
	@media ${({ theme }) => theme.device.laptop} {
	}

	h1 {
		width: 100%;

		font-weight: 600;
		color: #fff;
		font-size: 3.5rem;
		text-align: left;
		@media ${({ theme }) => theme.device.tablet} {
			width: 100%;
			font-family: 'Montserrat';
			font-weight: 600;
			color: #fff;
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
		@media ${({ theme }) => theme.device.mobile} {
			text-align: center;
			width: 100%;
			font-family: 'Montserrat';
			font-weight: 700;
			color: #fff;
			font-size: 50px;
			margin-top: 20px;
			line-height: 120%;
		}
	}
`;

const ContentWrap = styled.div`
	width: 80%;

	/* margin: 0 auto; */
	height: 600px;

	display: flex;
	flex-wrap: wrap;

	li {
		/* position: relative; */
		width: 50%;
		height: 100%;
		/* background-color: #1c6758; */
		border: 1px solid #fff;
		a {
			position: relative;
			width: 100%;
			height: 100%;
			display: flex;
			flex-direction: column;
			justify-content: flex-end;

			transition: all 0.1s;

			color: #fff;
		}
	}
`;

const Content01 = styled.li`
	width: 100%;
	height: 100%;
	background-color: #d6cda4;
`;
const Content02 = styled.li`
	width: 100%;
	height: 100%;
	background-color: #1c6758;
`;
const Content03 = styled.li`
	width: 100%;
	height: 100%;
	background-color: #3d8361;
`;
const Content04 = styled.li`
	width: 100%;
	height: 100%;
	background-color: #eef2e6;
`;

const ImgArea = styled.div`
	position: relative;
	width: 100%;
	height: 70%;
	display: flex;
	margin: 0 auto;

	img {
		width: 100%;
		height: 100%;
		opacity: 0.2;
		object-fit: cover;
	}
`;

// const TextWrap = styled.div`
// 	width: 50%;
// 	height: 100%;
// 	background-color: red;
// `;

const Text = styled.div`
	text-align: center;
	color: burlywood;
	p {
		color: #fff;
		font-size: 20px;
		background-color: burlywood;
		display: inline-block;

		border-radius: 50px;
		padding: 0.5em 1em;
	}
`;

const HoverWrap = styled.div`
	position: absolute;
	width: 50%;
	height: 50%;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	text-align: center;
	h3 {
		color: #fff;
		font-size: 3rem;
	}
	span {
		color: transparent;
	}
`;

const TextWrap = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
	height: 30%;
	background-color: ${({ theme }) => theme.colors.black_color};

	opacity: 0.5;
	p {
		color: #fff;
	}
`;
