  display: inline-block;

  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #3f72af;

  margin-bottom: 1px;
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  top: 35%;
  left: 15%;
  @media ${({ theme }) => theme.device.laptop} {
    left: 20%;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 2;
    position: absolute;
    top: 150px;
    left: 0;
    text-align: center;
    width: 100%;
  }
`;

const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  @media ${({ theme }) => theme.device.mobile} {
    position: relative;
    display: inline-block;
    margin: 0 2px;
  }
  &:hover {
    color: #000;
    &::after {
      content: "";
      display: block;
      width: 34px;
      height: 1px;
      background: #8e8f9a;

      position: absolute;

      top: 50%;
      left: -15%;

      transition: all 0.3s ease;
    }
  }
  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 80px;
    }
  }
  div {
    width: 34px;
    height: 1px;
    background: transparent;
    margin-bottom: 6px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      display: none;
    }
  }
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
  &:hover {
    color: #000;
  }
`;

